                var faceAxis = PMREMGenerator._sgFace2DMapping[faceIdx][PMREMGenerator.CP_FACEAXIS];
                PMREMGenerator._vectorTemp.x += faceAxis[0];
                PMREMGenerator._vectorTemp.y += faceAxis[1];
                PMREMGenerator._vectorTemp.z += faceAxis[2];
